import React, { useState } from "react";
import { Job } from "./JobContext";
import JobContext from "./JobContext";

interface Props {
  children: React.ReactNode;
}

const JobProvider: React.FC<Props> = ({ children }) => {
  const [jobs, setJobs] = useState<Job[]>([
    {
      id: 1,
      img: "data:image/png;base64,",
      name: "Vue.js",
      jobDescription: "JavaScript",
      jobLocation: "Banglore",
      requriedExperience: "4-5 yrs experience ",
      salary: "3 Lpa",
      jobRole: "Frontend Developer",
    },
    {
      id: 2,
      img: "data:image/png;base64,",
      name: "React.js",
      jobDescription: "JavaScript library",
      jobLocation: "Mumbai",
      requriedExperience: "3 years",
      salary: "4 Lpa",
      jobRole: "Frontend Developer",
    },
    {
      id: 3,
      img: "data:image/png;base64,",
      name: "Node.js",
      jobDescription: "Redux, Flux, ",
      jobLocation: "Hydrabad",
      requriedExperience: "3 years",
      salary: "5 Lpa",
      jobRole: "Frontend Developer",
    },
    {
      id: 4,
      img: "data:image/png;base64,",
      name: "Jest.js",
      jobDescription: "JavaScript testing",
      jobLocation: "Pune",
      requriedExperience: "2 years",
      salary: "6 Lpa",
      jobRole: "Frontend Developer",
    },
    {
      id: 5,
      img: "data:image/png;base64,",

      name: "Redux",
      jobDescription: "JavaScript",
      jobLocation: "Banglore",
      requriedExperience: "5 years",
      salary: "7 Lpa",
      jobRole: "Frontend Developer",
    },
    {
      id: 6,
      img: "data:image/png;base64,",

      name: "JQuery",
      jobDescription: "JavaScript library",
      jobLocation: "Mumbai",
      requriedExperience: "6 years",
      salary: "8 Lpa",
      jobRole: "Frontend Developer",
    },
    {
      id: 7,
      img: "data:image/png;base64,",

      name: "anime.js",
      jobDescription: "Redux, Flux,",
      jobLocation: "Hydrabad",
      requriedExperience: "3 years",
      salary: "9 Lpa",
      jobRole: "Frontend Developer",
    },
    {
      id: 8,
      img: "data:image/png;base64,",

      name: "Angular.js",
      jobDescription: "Redux, Flux,",
      jobLocation: "Hydrabad",
      requriedExperience: "3 years",
      salary: "9 Lpa",
      jobRole: "Frontend Developer",
    },
  ]);

  const addJob = (job: Job) => {
    setJobs((prevJobs) => [...prevJobs, job]);
  };

  const editJob = (id: number, EditedJob: Job) => {
    setJobs((prevJobs) => {
      const EditedJobs = prevJobs.map((job) =>
        job.id === id ? EditedJob : job
      );
      return EditedJobs;
    });
  };

  const deleteJob = (id: number) => {
    setJobs((prevJobs) => {
      const EditedJobs = prevJobs.filter((job) => job.id !== id);
      return EditedJobs;
    });
  };

  return (
    <JobContext.Provider value={{ jobs, addJob, editJob, deleteJob }}>
      {children}
    </JobContext.Provider>
  );
};

export default JobProvider;
